import React, { useState, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../../context/AuthContext";
import { useNavigate } from "react-router-dom";
import "./review.css";

const ReviewForm = ({ onNewReview }) => {
    const [credentials, setCredentials] = useState({
        name: "",
        email: "",
        asal: "",
        phone: "",
        comment: "",
        rating: 1, // Default rating
    });
    const { dispatch } = useContext(AuthContext);
    const navigate = useNavigate();

    const handleChange = (e) => {
        setCredentials({ ...credentials, [e.target.name]: e.target.value });
    };

    const handleReview = async (e) => {
        e.preventDefault();
        dispatch({ type: "REVIEW_START" });

        try {
            const url = "/reviews";
            const reviewData = { ...credentials, rating: parseInt(credentials.rating) }; // Ensure rating is a number
            console.log(`Posting to URL: ${url}`); // Log URL for debugging
            console.log('Review data:', reviewData); // Log data being sent
            const res = await axios.post(url, reviewData);
            console.log('Response:', res.data); // Log response from server
            dispatch({ type: "REVIEW_SUCCESS", payload: res.data });
            onNewReview(res.data); // Pass new review to parent component
            navigate("/");
        } catch (err) {
            console.error("Error submitting review:", err); // Debugging log
            const errorMessage = err.response?.data?.message || "Something went wrong. Please try again.";
            dispatch({ type: "REVIEW_FAILURE", payload: { message: errorMessage } });
            setTimeout(() => {
                dispatch({ type: "CLEAR_ERROR" });
            }, 5000);
        }
    };

    return (
        <form onSubmit={handleReview} className="formContainer">
            <input
                type="text"
                name="name"
                id="name"
                placeholder="Name"
                onChange={handleChange}
                className="lInput"
            />
            <input
                type="email"
                name="email"
                id="email"
                placeholder="Email"
                onChange={handleChange}
                className="lInput"
            />
            <input
                type="text"
                name="asal"
                id="asal"
                placeholder="Country"
                onChange={handleChange}
                className="lInput"
            />
            <input
                type="number"
                name="phone"
                id="phone"
                placeholder="Phone"
                onChange={handleChange}
                className="lInput"
            />
            <textarea
                name="comment"
                id="comment"
                placeholder="Comment"
                onChange={handleChange}
                className="lInput"
            />
            <label htmlFor="rating" className="lLabel">Rating</label>
            <select
                name="rating"
                id="rating"
                value={credentials.rating}
                onChange={handleChange}
                className="lInput"
            >
                {[1, 2, 3, 4, 5].map((num) => (
                    <option key={num} value={num}>{num}</option>
                ))}
            </select>
            <button
                type="submit"
                className="lButton"
            >
                Submit Review
            </button>
        </form>
    );
};

export default ReviewForm;
