import React, { useState, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../../context/AuthContext";
import { useNavigate } from "react-router-dom";
import "./review.css";
import logo from "../../assets/Logo GeBookIn .png";

const Review = () => {
    const [credentials, setCredentials] = useState({
        name: "",
        email: "",
        asal: "",
        phone: "",
        comment: "",
    });
    const { loading, error, dispatch } = useContext(AuthContext);
    const navigate = useNavigate();

    const handleChange = (e) => {
        setCredentials({ ...credentials, [e.target.name]: e.target.value });
    };

    const handleReview = async (e) => {
        e.preventDefault();
        dispatch({ type: "REVIEW_START" });

        try {
            const url = "http://localhost:3001/api/v1/reviews";
            console.log(`Posting to URL: ${url}`); // Log URL for debugging
            const res = await axios.post(url, ...credentials);
            dispatch({ type: "REVIEW_SUCCESS", payload: res.data });
            navigate("/");
        } catch (err) {
            console.error("Error submitting review:", err); // Debugging log
            dispatch({ type: "REVIEW_FAILURE", payload: err.response.data });
            setTimeout(() => {
                dispatch({ type: "CLEAR_ERROR" });
            }, 5000);
        }
    };


    return (
        <div className="review">
            <div className="lContainer">
                <div className="logoContainer">
                    <img src={logo} alt="Logo" className="logo" />
                </div>
                <form onSubmit={handleReview} className="formContainer">
                    <input
                        type="text"
                        name="name"
                        id="name"
                        placeholder="Name"
                        onChange={handleChange}
                        className="lInput"
                    />
                    <input
                        type="email"
                        name="email"
                        id="email"
                        placeholder="Email"
                        onChange={handleChange}
                        className="lInput"
                    />
                    <input
                        type="text"
                        name="asal"
                        id="asal"
                        placeholder="Country"
                        onChange={handleChange}
                        className="lInput"
                    />
                    <input
                        type="number"
                        name="phone"
                        id="phone"
                        placeholder="Phone"
                        onChange={handleChange}
                        className="lInput"
                    />
                    <textarea
                        name="comment"
                        id="comment"
                        placeholder="Comment"
                        onChange={handleChange}
                        className="lInput"
                    />
                    <button
                        type="submit"
                        disabled={loading}
                        className="lButton"
                    >
                        Submit Review
                    </button>
                    {error && (
                        <div className="errorContainer">
                            <span className="error">{error.message}</span>
                        </div>
                    )}
                </form>
            </div>
        </div>
    );
};

export default Review;
