import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../../context/AuthContext";
import { useNavigate } from "react-router-dom";
import "./review.css";
import logo from "../../assets/Logo GeBookIn .png";

const Review = () => {
    const [credentials, setCredentials] = useState({
        name: "",
        email: "",
        asal: "",
        phone: "",
        comment: "",
        rating: 1, // Default rating
    });
    const [reviews, setReviews] = useState([]); // State for storing reviews
    const { loading, error, dispatch } = useContext(AuthContext);
    const navigate = useNavigate();

    // Fetch reviews when component mounts
    useEffect(() => {
        const fetchReviews = async () => {
            try {
                const res = await axios.get("/reviews");
                setReviews(res.data);
            } catch (err) {
                console.error("Error fetching reviews:", err);
            }
        };

        fetchReviews();
    }, []);

    const handleChange = (e) => {
        setCredentials({ ...credentials, [e.target.name]: e.target.value });
    };

    const handleReview = async (e) => {
        e.preventDefault();
        dispatch({ type: "REVIEW_START" });

        try {
            const url = "/reviews";
            console.log(`Posting to URL: ${url}`); // Log URL for debugging
            console.log('Review data:', credentials); // Log data being sent
            const res = await axios.post(url, credentials);
            console.log('Response:', res.data); // Log response from server
            dispatch({ type: "REVIEW_SUCCESS", payload: res.data });
            setReviews(prevReviews => [...prevReviews, res.data]); // Add new review to state
            navigate("/");
        } catch (err) {
            console.error("Error submitting review:", err); // Debugging log
            const errorMessage = err.response?.data?.message || "Something went wrong. Please try again.";
            dispatch({ type: "REVIEW_FAILURE", payload: { message: errorMessage } });
            setTimeout(() => {
                dispatch({ type: "CLEAR_ERROR" });
            }, 5000);
        }
    };

    // Calculate the average rating
    const averageRating = reviews.length > 0
        ? (reviews.reduce((sum, review) => sum + review.rating, 0) / reviews.length).toFixed(1)
        : 0;

    return (
        <div className="review">
            <div className="lContainer">
                <div className="logoContainer">
                    <img src={logo} alt="Logo" className="logo" />
                </div>
                <form onSubmit={handleReview} className="formContainer">
                    <input
                        type="text"
                        name="name"
                        id="name"
                        placeholder="Name"
                        onChange={handleChange}
                        className="lInput"
                    />
                    <input
                        type="email"
                        name="email"
                        id="email"
                        placeholder="Email"
                        onChange={handleChange}
                        className="lInput"
                    />
                    <input
                        type="text"
                        name="asal"
                        id="asal"
                        placeholder="Country"
                        onChange={handleChange}
                        className="lInput"
                    />
                    <input
                        type="number"
                        name="phone"
                        id="phone"
                        placeholder="Phone"
                        onChange={handleChange}
                        className="lInput"
                    />
                    <textarea
                        name="comment"
                        id="comment"
                        placeholder="Comment"
                        onChange={handleChange}
                        className="lInput"
                    />
                    <label htmlFor="rating" className="lLabel">Rating</label>
                    <select
                        name="rating"
                        id="rating"
                        value={credentials.rating}
                        onChange={handleChange}
                        className="lInput"
                    >
                        {[1, 2, 3, 4, 5].map((num) => (
                            <option key={num} value={num}>{num}</option>
                        ))}
                    </select>
                    <button
                        type="submit"
                        disabled={loading}
                        className="lButton"
                    >
                        Submit Review
                    </button>
                    {error && (
                        <div className="errorContainer">
                            <span className="error">{error.message}</span>
                        </div>
                    )}
                </form>

                {/* Display the average rating */}
                <div className="averageRatingContainer">
                    <h2>Average Rating: {averageRating}</h2>
                </div>

                {/* Display the reviews */}
                <div className="reviewsContainer">
                    {reviews.length > 0 ? (
                        reviews.map(review => (
                            <div key={review._id} className="reviewItem">
                                <h3>{review.name}</h3>
                                <p>Email: {review.email}</p>
                                <p>Country: {review.asal}</p>
                                <p>Phone: {review.phone}</p>
                                <p>Comment: {review.comment}</p>
                                <p>Rating: {review.rating}</p>
                                <p>Date: {new Date(review.date).toLocaleDateString()}</p>
                            </div>
                        ))
                    ) : (
                        <p>No reviews available.</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Review;
