import React, { useState } from 'react';
import axios from 'axios';

const ReviewPage = () => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [review, setReview] = useState('');
    const [rating, setRating] = useState(0);
    const [submitted, setSubmitted] = useState(false);

    const handleSubmit = async (event) => {
        event.preventDefault();
        const reviewData = { name, email, review, rating };
        try {
            const response = await axios.post('http://localhost:3001/api/v1/reviews', reviewData);
            setSubmitted(true);
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div className="review-page">
            <h1>Leave a Review</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Name:
                    <input type="text" value={name} onChange={(event) => setName(event.target.value)} />
                </label>
                <br />
                <label>
                    Email:
                    <input type="email" value={email} onChange={(event) => setEmail(event.target.value)} />
                </label>
                <br />
                <label>
                    Review:
                    <textarea value={review} onChange={(event) => setReview(event.target.value)} />
                </label>
                <br />
                <label>
                    Rating:
                    <select value={rating} onChange={(event) => setRating(event.target.value)}>
                        <option value="1">1</option>
                        <option value="2">2</option>
                        <option value="3">3</option>
                        <option value="4">4</option>
                        <option value="5">5</option>
                    </select>
                </label>
                <br />
                <button type="submit">Submit Review</button>
            </form>
            {submitted? (
                <p>Thank you for submitting your review!</p>
            ) : (
                <p>Please fill out the form to leave a review.</p>
            )}
        </div>
    );
};

export default ReviewPage;
