import React, { useState, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../../context/AuthContext";
import { useNavigate } from "react-router-dom";
import "./review.css";
import logo from "../../assets/Logo GeBookIn .png";

const ReviewForm = ({ onNewReview }) => {
    const [credentials, setCredentials] = useState({
        name: "",
        email: "",
        asal: "",
        phone: "",
        comment: "",
    });
    const { loading, error, dispatch } = useContext(AuthContext);
    const navigate = useNavigate();

    const handleChange = (e) => {
        setCredentials({ ...credentials, [e.target.name]: e.target.value });
    };

    const handleReview = async (e) => {
        e.preventDefault();
        dispatch({ type: "REVIEW_START" });

        try {
            const url = "/reviews";
            const res = await axios.post(url, credentials);
            dispatch({ type: "REVIEW_SUCCESS", payload: res.data });
            onNewReview(res.data); // Add new review to the list
            navigate("/");
        } catch (err) {
            const errorMessage = err.response?.data?.message || "Something went wrong. Please try again.";
            dispatch({ type: "REVIEW_FAILURE", payload: { message: errorMessage } });
            setTimeout(() => {
                dispatch({ type: "CLEAR_ERROR" });
            }, 5000);
        }
    };

    return (
        <div className="formContainer">
            <div className="logoContainer">
                <img src={logo} alt="Logo" className="logo" />
            </div>
            <form onSubmit={handleReview}>
                <input
                    type="text"
                    name="name"
                    id="name"
                    placeholder="Name"
                    onChange={handleChange}
                    className="lInput"
                />
                <input
                    type="email"
                    name="email"
                    id="email"
                    placeholder="Email"
                    onChange={handleChange}
                    className="lInput"
                />
                <input
                    type="text"
                    name="asal"
                    id="asal"
                    placeholder="Country"
                    onChange={handleChange}
                    className="lInput"
                />
                <input
                    type="number"
                    name="phone"
                    id="phone"
                    placeholder="Phone"
                    onChange={handleChange}
                    className="lInput"
                />
                <textarea
                    name="comment"
                    id="comment"
                    placeholder="Comment"
                    onChange={handleChange}
                    className="lInput"
                />
                <button
                    type="submit"
                    disabled={loading}
                    className="lButton"
                >
                    Submit Review
                </button>
                {error && (
                    <div className="errorContainer">
                        <span className="error">{error.message}</span>
                    </div>
                )}
            </form>
        </div>
    );
};

export default ReviewForm;
